import React from "react";
import "../contact.css";

import emailjs from '@emailjs/browser';



  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
    }





export default function Contact() {
  return (

    <div style={{
        margin: "60px 0"
    }}>
      <h2 className="text-4xl font-bold mb-5 border-b-[5px] w-[200px] mx-auto border-Blue Stone-600 pb-2">
  Contact Me
</h2>
<form ref={form} onSubmit={sendEmail}></form>
      <form autoComplete="off">
        <input
          name="name"
          type="text"
          className="feedback-input"
          placeholder="Name"
        />
        <input
          name="email"
          type="text"
          className="feedback-input"
          placeholder="Email"
        />
        <textarea
          name="text"
          className="feedback-input"
          placeholder="Comment"
        ></textarea>
        <input type="submit" value="SUBMIT" />
      </form>
    </div>
  );
}
